import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'

let synth
let chorus
let pingPongDelay

export default class Container extends Component {
  constructor(props) {
    super(props)

    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sawtooth',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const pingPongDelaySettings = { wet: 0.2, delayTime: 0.25, maxDelayTime: 1 }

    const chorusSettings = {
      wet: 0.3,
      type: 'sine',
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      spread: 180
    }

    this.state = {
      synthSettings,
      pingPongDelaySettings,
      chorusSettings
    }
  }

  handleStart = () => {
    const { synthSettings, pingPongDelaySettings, chorusSettings } = this.state
    synth = new Tone.Synth(synthSettings)

    const chorus = new Tone.Chorus(chorusSettings)

    pingPongDelay = new Tone.PingPongDelay(
      pingPongDelaySettings
    ).toDestination()

    synth.chain(chorus, pingPongDelay)

    const sequence = [
      {
        time: '0:0:0',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:0:2',
        noteName: 'A3',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:1:0',
        noteName: 'E3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:2:0',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:0',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:1',
        noteName: 'E3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:2',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:0:0',
        noteName: 'D3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:0',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:2',
        noteName: 'E4',
        duration: '4n',
        velocity: 0.7
      },
      {
        time: '1:1:3',
        noteName: 'D4',
        duration: '4n',
        velocity: 0.8
      },
      {
        time: '1:2:0',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:0',
        noteName: 'G3',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:2',
        noteName: 'C4',
        duration: '4n',
        velocity: 1
      }
    ]

    const part = new Tone.Part((time, note) => {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence).start(0)

    // Указываем длительность партии
    part.loopEnd = '2m'

    // Включаем зацикливание
    part.loop = true

    // Включаем звук в браузере
    // sampler.context.resume()

    // Включаем отсчёт времени в Tone.js
    Tone.Transport.start()
  }


  //выносить лучше создание, чем изменение(то есть лучше вынести в другой файл звуки и т.д.)

  handleValueChange = (property, value) => { //чтобы мы не писали каждый раз повторяющийся код, создаем функцию, где нужно менять только условия для разных property
    const { synthSettings, pingPongDelaySettings} = this.state
    if(property=== 'synthType'){  //domain naming design
      synth.oscillator.type = value
      synthSettings.oscillator.type = value
    }else if (property === 'pingPongdelayWet'){
      pingPongDelay.wet.value = value
      pingPongDelaySettings.wet = value
    }else if (property === 'chorusWet'){
      chorus.wet.value = value
      chorusSettings.wet= value
    }

    this.setState({
    synthSettings,
    pingPongDelaySettings,
    chorusSettings
    })
  }



  render() {
    const { synthSettings, pingPongDelaySettings, chorusSettings } = this.state
    const options = ['sine', 'square', 'sawtooth', 'triangle']

    return (
      <div className="Container">
        <SC_Button
          text="Art Design Coding Community"
          handleClick={this.handleStart}
        />

        <SC_ToggleButtonSet
          name="Type"
          options={options}
          value={synthSettings.oscillator.type}
          property="synthType"
          handleChange={this.handleOscillatorTypeChange}
        />

        <SC_Slider
          name="Delay Wet"
          min={0}
          max={1}
          step={0.01}
          value={pingPongDelaySettings.wet}
          property="pingPongdelayWet"
          handleChange={this.handleDelayWetValueChange}
        />

        <SC_Slider
          name="chorusWet"
          min={0}
          max={1}
          step={0.01}
          value={chorusSettings.wet}
          property="chorusWet"
          handleChange={this.handleDelayWetValueChange}
        />
      </div>
    )
  }
}


//мы не можем менять pingPondDelay wet value с помощью handleOschillatorTypeChange, 		
//потому что мы там меняем только осциллятор, поэтому мы создаем функцию handleDelayWetValueChange, 		
//которая будет отвечать за pingpongDelay wet
