import * as Tone from 'tone'
import React, { Component } from 'react'

import SC_Button from './SC_Button'
import SC_Button2 from './SC_Button2'
import SC_ToggleButtonSet from './SC_ToggleButtonSet'
import SC_Slider from './SC_Slider'

let synth
/////////////
let synth2
let chorus
let pingPongDelay
//////////////
let chebyshev2
/////////////
let vibrato2

export default class Container extends Component {
  constructor(props) {
    super(props)

    const synthSettings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sawtooth',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    ////////////////////////
    const synth2Settings = {
      volume: -20,
      detune: 0,
      portamento: 0.05,
      envelope: {
        attack: 0.05,
        attackCurve: 'exponential',
        decay: 0.2,
        decayCurve: 'exponential',
        sustain: 0.2,
        release: 1.5,
        releaseCurve: 'exponential'
      },
      oscillator: {
        type: 'sawtooth',
        modulationType: 'sine',
        // partialCount: 0,
        // partials: [],
        phase: 0,
        harmonicity: 0.5
      }
    }

    const chorusSettings = {
      wet: 0.3,
      type: 'sine',
      frequency: 1.5,
      delayTime: 3.5,
      depth: 0.7,
      spread: 180
    }

    const pingPongDelaySettings = { wet: 0.2, delayTime: 0.25, maxDelayTime: 1 }

//////////////////
    const chebyshev2Settings = {
      wet: 0.2,
      order: 50,
      oversample: 'none'
    }

    ////////////////
    const vibrato2Settings = {
      wet: 0,
      maxDelay: 0.005,
      frequency: 5,
      depth: 0.1,
      type: 'sine'
    }



    this.state = {
      synthSettings,
      ///////////////
      synth2Settings,
      chorusSettings,
      pingPongDelaySettings,

      /////////////////////
      chebyshev2Settings,
      /////////////////////
      vibrato2Settings
    }
  }

  handleStart = () => {
    const { synthSettings, synth2Settings, chorusSettings, pingPongDelaySettings, chebyshev2Settings, vibrato2Settings } = this.state

    synth = new Tone.Synth(synthSettings)

    chorus = new Tone.Chorus(chorusSettings).start()

    pingPongDelay = new Tone.PingPongDelay(
      pingPongDelaySettings
    ).toDestination()

    synth.chain(chorus, pingPongDelay)


    const sequence = [
      {
        time: '0:0:0',
        noteName: 'C4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:0:2',
        noteName: 'C4',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:1:0',
        noteName: 'G4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:2:0',
        noteName: 'G4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:0',
        noteName: 'A4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:1',
        noteName: 'A4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:2',
        noteName: 'G4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:0:0',
        noteName: 'F4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:0',
        noteName: 'F4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:2',
        noteName: 'E4',
        duration: '4n',
        velocity: 0.7
      },
      {
        time: '1:1:3',
        noteName: 'E4',
        duration: '4n',
        velocity: 0.8
      },
      {
        time: '1:2:0',
        noteName: 'D4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:0',
        noteName: 'D4',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:2',
        noteName: 'C4',
        duration: '4n',
        velocity: 1
      }
    ]


    const part = new Tone.Part((time, note) => {
      synth.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence).start(0)

    part.loopEnd = '2m'
    part.loop = true
    Tone.Transport.start()

  }

  handleStart2 = () => {
    const { synthSettings, synth2Settings, chorusSettings, pingPongDelaySettings, chebyshev2Settings, vibrato2Settings } = this.state


    ////////////////////////
    synth2 = new Tone.Synth(synth2Settings)

    ///////////////////////
    chebyshev2 = new Tone.Chebyshev(chebyshev2Settings)
    //////////////////////
    vibrato2 = new Tone.Vibrato(vibrato2Settings)

    synth.chain(chorus, pingPongDelay)
    ///////////////////////////
    synth2.chain(chebyshev2, vibrato2)


    ////////////////////
    const sequence2 = [
      {
        time: '0:0:0',
        noteName: 'C2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:0:2',
        noteName: 'A2',
        duration: '1n',
        velocity: 1
      },
      {
        time: '0:1:0',
        noteName: 'E2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:2:0',
        noteName: 'G2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:0',
        noteName: 'C2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:1',
        noteName: 'E2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '0:3:2',
        noteName: 'G2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:0:0',
        noteName: 'D2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:0',
        noteName: 'G2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:1:2',
        noteName: 'E3',
        duration: '4n',
        velocity: 0.7
      },
      {
        time: '1:1:3',
        noteName: 'D3',
        duration: '4n',
        velocity: 0.8
      },
      {
        time: '1:2:0',
        noteName: 'C2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:0',
        noteName: 'G2',
        duration: '4n',
        velocity: 1
      },
      {
        time: '1:3:2',
        noteName: 'C3',
        duration: '4n',
        velocity: 1
      }
    ]




    /////////////////////////////
    const part2 = new Tone.Part((time, note) => {
      synth2.triggerAttackRelease(
        note.noteName,
        note.duration,
        time,
        note.velocity
      )
    }, sequence2).start(0)

    part2.loopEnd = '2m'
    part2.loop = true
    Tone.Transport.start()
  }


  handleValueChange = (property, value) => {
    const { synthSettings, synth2Settings, chorusSettings, pingPongDelaySettings, chebyshev2Settings, vibrato2Settings } = this.state

    if (property === 'synthType') {
      synth.oscillator.type = value
      synthSettings.oscillator.type = value
    } else if (property === 'pingPongDelayWet') {
      pingPongDelay.wet.value = value
      pingPongDelaySettings.wet = value
    } else if (property === 'chorusWet') {
      chorus.wet.value = value
      chorusSettings.wet = value
    }
    /////////////////
    else if (property === 'synth2Type'){
      synth2.oscillator.type = value
      synth2Settings.oscillator.type = value
    }
    /////////////////
    else if (property === 'chebyshev2Wet'){
      chebyshev2.wet.value = value
      chebyshev2Settings.wet = value
    }
    /////////////////
    else if (property === 'vibrato2Wet'){
      vibrato2.wet.value = value
      vibrato2Settings.wet = value
    }

    this.setState({
      synthSettings,
      ///////////////////
      synth2Settings,
      chorusSettings,
      pingPongDelaySettings,

      ///////////////////
      chebyshev2Settings,
      //////////////////
      vibrato2Settings
    })
  }

  render() {
    const { synthSettings, synth2Settings, chorusSettings, pingPongDelaySettings, chebyshev2Settings, vibrato2Settings } = this.state

    const options = ['sine', 'square', 'sawtooth', 'triangle']

    return (
      <div className="Container">
        <SC_Button
          text="Synth1"
          handleClick={this.handleStart}
        />
        <SC_Button2
          text="Synth2"
          handleClick={this.handleStart2}
        />

        <SC_ToggleButtonSet
          name="Type"
          options={options}
          value={synthSettings.oscillator.type}
          property="synthType"
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Delay Wet"
          min={0}
          max={1}
          step={0.01}
          value={pingPongDelaySettings.wet}
          property="pingPongDelayWet"
          handleChange={this.handleValueChange}
        />

        <SC_Slider
          name="Chorus Wet"
          min={0}
          max={1}
          step={0.01}
          value={chorusSettings.wet}
          property="chorusWet"
          handleChange={this.handleValueChange}
        />


        <SC_ToggleButtonSet
          name="Synth 2 Type"
          options={options}
          value={synth2Settings.oscillator.type}
          property="synth2Type"
          handleChange={this.handleValueChange}
        />  



        <SC_Slider
          name="Chebyshev 2 Wet"
          min={0}
          max={1}
          step={0.01}
          value={chebyshev2Settings.wet}
          property="chebyshev2Wet"
          handleChange={this.handleValueChange}
        />
        <SC_Slider
          name="Vibrato 2 Wet"
          min={0}
          max={1}
          step={0.01}
          value={vibrato2Settings.wet}
          property="vibrato2Wet"
          handleChange={this.handleValueChange}
        />
      </div>

    )
  }
}
