import React, { Component } from 'react';
import SC_Button from './SC_Button';
import SC_ToggleButtonSet from './SC_ToggleButtonSet';


export default class Container extends Component {
  constructor(props) {
    super(props)

    let audioCtx

    let oscillator = {
        settings: {
            type:'square'
        },
        node: {}

    }

    this.state = {
        audioCtx,
        oscillator
    }
  }

  createNewOscillator = () => {
    // create web audio api context
    const audioCtx = new (window.AudioContext || window.webkitAudioContext)()

    // create Oscillator node
    const oscillatorNode = audioCtx.createOscillator()

    const { oscillator} = this.state

    oscillatorNode.type = oscillator.settings.type
    oscillatorNode.frequency.setValueAtTime(440, audioCtx.currentTime) // value in hertz
    oscillatorNode.connect(audioCtx.destination) // включаем звук в провод, без этого не будет работать
    oscillatorNode.start()

    this.setState({ 
        audioCtx, 
        oscillator: {   // ссылка к оссилятору в браузере
            settings: {
                type: oscillator.settings.type
            },
            node: oscillatorNode
        }})
  }

  handleOscillatorPropertyChange = (property, value) => {
      const { audioCtx, oscillator } = this.state;

   if (property === 'type'){ 
      oscillator.node.type = value
      oscillator.settings.type = value

      this.setState({
          oscillator
      })
    }
    if (property === 'frequency'){

    }
  }

  render() {
      const options = ['sign', 'square', 'sawtooth', 'triangle'];
      const {oscillator} = this.state

    return (
      <div className="Container">
          <SC_Button 
            text='Art Design Coding Community' 
            handleClick={this.createNewOscillator}/>

          <SC_ToggleButtonSet 
            name='type' 
            options={options}
            value={oscillator.settings.type}
            property='type'
            handleChange={this.handleOscillatorPropertyChange}
            />
      </div>
    )
  }
}

//когда мы хотим изменить звучание обращаемся к объекту (oscillatorNode)
//когда хотим менять настройки, обращаемся к настройкам
//настройки лежат отдельно от объекта.
